mapper.insert:
INSERT INTO delivery_order (
    id,delivery_no, order_id, order_no, user_id, product_id, product_name, quantity,
    receiver_name, receiver_phone, receiver_address, delivery_status, delivery_company,
    delivery_number, delivery_time, receive_time, remark
) VALUES (
             #{id},#{deliveryNo}, #{orderId}, #{orderNo}, #{userId}, #{productId}, #{productName}, #{quantity},
             #{receiverName}, #{receiverPhone}, #{receiverAddress}, #{deliveryStatus}, #{deliveryCompany},
             #{deliveryNumber}, #{deliveryTime}, #{receiveTime}, #{remark}
         )

mapper.update:
UPDATE delivery_order
delivery_status = #{deliveryStatus}, delivery_company = #{deliveryCompany}, delivery_number = #{deliveryNumber}, delivery_time = #{deliveryTime}, receive_time = #{receiveTime}, remark = #{remark},
WHERE id = #{id}

mapper.select:
SELECT 
FROM delivery_order
WHERE 1=1

    AND delivery_no = #{deliveryNo}


    AND order_no = #{orderNo}


    AND user_id = #{userId}


    AND delivery_status = #{deliveryStatus}


    AND create_time >= #{startTime}


    AND create_time <= #{endTime}

ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(1)
FROM delivery_order
WHERE 1=1

    AND delivery_no = #{deliveryNo}


    AND order_no = #{orderNo}


    AND user_id = #{userId}


    AND delivery_status = #{deliveryStatus}


    AND create_time >= #{startTime}


    AND create_time <= #{endTime}

mapper.update:
UPDATE delivery_order
