mapper.insert:
INSERT INTO order_info (
    id,order_no, user_id, product_id, product_name, product_price, quantity,
    original_amount, discount_amount, pay_amount, order_status, pay_status,
    order_type, remark
) VALUES (
             #{id},#{orderNo}, #{userId}, #{productId}, #{productName}, #{productPrice}, #{quantity},
             #{originalAmount}, #{discountAmount}, #{payAmount}, #{orderStatus}, #{payStatus},
             #{orderType}, #{remark}
         )

mapper.update:
UPDATE order_info
order_status = #{orderStatus}, pay_status = #{payStatus}, pay_time = #{payTime}, pay_type = #{payType}, transaction_id = #{transactionId}, cancel_reason = #{cancelReason}, cancel_time = #{cancelTime}, delivery_time = #{deliveryTime}, receive_time = #{receiveTime}, close_time = #{closeTime}, remark = #{remark},
WHERE id = #{id}

mapper.select:
SELECT 
FROM order_info
WHERE 1=1

    AND order_no = #{orderNo}


    AND user_id = #{userId}


    AND product_name LIKE CONCAT('%', #{productName}, '%')


    AND order_status = #{orderStatus}


    AND pay_status = #{payStatus}


    AND create_time >= #{startTime}


    AND create_time <= #{endTime}

ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(1)
FROM order_info
WHERE 1=1

    AND order_no = #{orderNo}


    AND user_id = #{userId}


    AND product_name LIKE CONCAT('%', #{productName}, '%')


    AND order_status = #{orderStatus}


    AND pay_status = #{payStatus}


    AND create_time >= #{startTime}


    AND create_time <= #{endTime}
