mapper.insert:
INSERT INTO user_coupon (
    id,user_id, coupon_id, coupon_code, status, order_id, use_time,
    start_time, end_time
) VALUES (
             #{id},#{userId}, #{couponId}, #{couponCode}, #{status}, #{orderId}, #{useTime},
             #{startTime}, #{endTime}
         )

mapper.update:
UPDATE user_coupon
status = #{status}, order_id = #{orderId}, use_time = #{useTime}, start_time = #{startTime}, end_time = #{endTime},
WHERE id = #{id}

mapper.select:
SELECT 
FROM user_coupon
WHERE user_id = #{userId}
AND coupon_id = #{couponId}
ORDER BY create_time DESC

SELECT 
FROM user_coupon
WHERE user_id = #{userId}

    AND status = #{status}

ORDER BY create_time DESC

